<?php\n\nrequire 'vendor/autoload.php';\n\nuse League\Uri\Http;\n\n$url = Http::createFromString('https://example.com/path?query=foo#fragment');\n\n// Get parts of the URL\necho $url->getScheme();   // https\necho $url->getHost();     // example.com\necho $url->getPath();     // /path\necho $url->getQuery();    // query=foo\necho $url->getFragment(); // fragment\n\n// Modify parts of the URL\n$newUrl = $url->withPath('/newpath')->withQuery('query=bar');\necho $newUrl; // https://example.com/newpath?query=bar#fragment\n\n// Resolve relative paths\n$baseUrl = Http::createFromString('https://example.com/base/');\n$relativeUrl = Http::createFromString('/relative/path');\n$resolvedUrl = $baseUrl->resolve($relativeUrl);\necho $resolvedUrl; // https://example.com/relative/path