<?php\nfunction removeDuplicates($array) {\n    $serializedArray = array_map('serialize', $array);\n    $uniqueArray = array_unique($serializedArray);\n    $resultArray = array_map('unserialize', $uniqueArray);\n    \n    return $resultArray;\n}\n\n$inputArray = [\n    ['abc', 'def'],\n    ['ghi', 'jkl'],\n    ['mno', 'pql'],\n    ['abc', 'def'],\n    ['ghi', 'jkl'],\n    ['mno', 'pql']\n];\n\n$result = removeDuplicates($inputArray);\nprint_r($result);\n?>