<?php\nfunction isLocalUrl($url, $baseUrl) {\n    // Parse the URL\n    $parsedUrl = parse_url($url);\n\n    // If the URL has a scheme, it is an absolute URL\n    if (isset($parsedUrl['scheme'])) {\n        return false; // External link\n    }\n\n    // If the URL does not have a scheme, it is a relative URL\n    // Resolve it against the base URL\n    $baseParsedUrl = parse_url($baseUrl);\n    \n    // Build the resolved URL\n    $resolvedUrl = $baseParsedUrl['scheme'] . '://' . $baseParsedUrl['host'];\n\n    if (isset($baseParsedUrl['port'])) {\n        $resolvedUrl .= ':' . $baseParsedUrl['port'];\n    }\n\n    if (isset($baseParsedUrl['path'])) {\n        $basePath = rtrim(dirname($baseParsedUrl['path']), '/');\n        $resolvedUrl .= $basePath . '/' . ltrim($url, '/');\n    } else {\n        $resolvedUrl .= '/' . ltrim($url, '/');\n    }\n\n    // Return the resolved URL\n    return $resolvedUrl;\n}\n\n// Example usage\n$baseUrl = "http://example.com/folder/page.html";\n$urls = [\n    "../folder/folder2/blah/page.html",\n    "http://google.com",\n    "page.html"\n];\n\nforeach ($urls as $url) {\n    $resolvedUrl = isLocalUrl($url, $baseUrl);\n    if ($resolvedUrl) {\n        echo "Local: " . $resolvedUrl . "\n";\n    } else {\n        echo "External: " . $url . "\n";\n    }\n}\n?>