<?php\nuse PHPMailer\PHPMailer\PHPMailer;\nuse PHPMailer\PHPMailer\Exception;\n\n// Load Composer's autoloader\nrequire 'vendor/autoload.php';\n\n$mail = new PHPMailer(true);\n\ntry {\n    //Server settings\n    $mail->isSMTP();\n    $mail->Host = 'mail.domain.com';  // Specify main and backup SMTP servers\n    $mail->SMTPAuth = true;\n    $mail->Username = 'user';        // SMTP username\n    $mail->Password = 'pass';        // SMTP password\n    $mail->SMTPSecure = 'tls';       // Enable TLS encryption, `ssl` also accepted\n    $mail->Port = 587;               // TCP port to connect to\n\n    //Recipients\n    $mail->setFrom('no-reply@domain.com', 'Mailer');\n    $mail->addAddress('fs@domain.com');     // Add a recipient\n    $mail->addAddress('students@domain.com'); // Add another recipient\n\n    //Content\n    $mail->isHTML(true);                                  // Set email format to HTML\n    $mail->Subject = 'Here is the subject';\n    $mail->Body    = 'This is the HTML message body <b>in bold!</b>';\n    $mail->AltBody = 'This is the body in plain text for non-HTML mail clients';\n\n    $mail->send();\n    echo 'Message has been sent';\n} catch (Exception $e) {\n    echo "Message could not be sent. Mailer Error: {$mail->ErrorInfo}";\n}