<?php\n// Sample string containing Unicode escape sequences\n$inputString = '\u5353\u8d8a\u4e9a\u9a6c\u900a: \u7f51\u4e0a\u8d2d\u7269: \u5728\u7ebf\u9500\u552e\u56fe\u4e66\uff0cDVD\uff0cCD\uff0c\u6570\u7801\uff0c\u73a9\u5177\uff0c\u5bb6\u5c45\uff0c\u5316\u5986';\n\n// Function to decode Unicode escape sequences\nfunction decodeUnicodeEscapeSequences($str) {\n    return preg_replace_callback('/\\\\u([0-9a-fA-F]{4})/', function ($matches) {\n        return mb_convert_encoding(pack('H*', $matches[1]), 'UTF-8', 'UCS-2BE');\n    }, $str);\n}\n\n// Decode the input string\n$decodedString = decodeUnicodeEscapeSequences($inputString);\n\n// Display the decoded string in an HTML page\necho htmlspecialchars($decodedString, ENT_QUOTES, 'UTF-8');