<?php\nfunction add_query_params($url, $params) {\n    $parsed_url = parse_url($url);\n    parse_str($parsed_url['query'] ?? '', $queryArray);\n    $newQueryArray = array_merge($queryArray, $params);\n    $newQueryString = http_build_query($newQueryArray);\n    return $parsed_url['path'] . '?' . $newQueryString;\n}\n\n// Current URL with existing parameters\n$current_url = "example.com?" . $_SERVER['QUERY_STRING'];\n\n// Adding new parameters\n$new_params = array('bar' => 'xyz');\n\n$new_url = add_query_params($current_url, $new_params);\necho $new_url;  // Output: example.com/?foo=abc&bar=xyz\n?>