// File: app/controllers/my_controller.php\n\nApp::import('Config', 'database');\n\nclass MyController extends AppController {\n    public function myAction() {\n        // Instantiate the DATABASE_CONFIG class\n        $dbConfig = new DATABASE_CONFIG();\n        \n        // Access the 'db1' configuration array\n        $db1Config = $dbConfig->db1;\n        \n        // Retrieve the login and password values\n        $login = $db1Config['login'];\n        $password = $db1Config['password'];\n        \n        // Now you can use $login and $password as needed\n        $this->set('login', $login);\n        $this->set('password', $password);\n    }\n}