$testPics = takeFiles($picsDir, "/\.(jpg|jpeg|png|gif)$/i");\n\nfunction takeFiles($dir, $rex="") {\n    $dir .= "/";\n    $files = array();\n    $dp = opendir($dir);\n    while ($file = readdir($dp)) {\n        if ($file == '.' || $file == '..' || is_dir($dir . $file)) continue;\n        if ($rex != "" && !preg_match($rex, $file)) continue;\n        $files[] = $file;\n    }\n    closedir($dp);\n    return $files;\n}