function isPointInPolygon($point, $polygon) {\n    $x = $point['lat'];\n    $y = $point['lng'];\n    $inside = false;\n    for ($i = 0, $j = count($polygon) - 1; $i < count($polygon); $j = $i++) {\n        $xi = $polygon[$i]['lat'];\n        $yi = $polygon[$i]['lng'];\n        $xj = $polygon[$j]['lat'];\n        $yj = $polygon[$j]['lng'];\n        \n        $intersect = (($yi > $y) != ($yj > $y)) &&\n                     ($x < ($xj - $xi) * ($y - $yi) / ($yj - $yi) + $xi);\n        if ($intersect) {\n            $inside = !$inside;\n        }\n    }\n    return $inside;\n}