$output = [];\n$return_var = 0;\n\n// Execute the command\nexec('ln -s ' . escapeshellarg(PLUGIN_DIR . '/.htaccess') . ' ' . escapeshellarg(ABSPATH . '/.htaccess'), $output, $return_var);\n\n// Check for errors\nif ($return_var !== 0) {\n    // Display the error message\n    echo "Error executing command:\n";\n    echo implode("\n", $output);\n} else {\n    echo "Command executed successfully.";\n}