<?php\n$monthlybill = $_GET['monthlybill'];\ninclude('values.php'); // Include the file with offer savings values\n\necho "Your monthly bill was $monthlybill";\n\n// Create an array of offer savings values\n$offers = [\n    1 => $offer1calcsavings,\n    2 => $offer2calcsavings,\n    3 => $offer3calcsavings,\n    4 => $offer4calcsavings,\n    5 => $offer5calcsavings,\n    6 => $offer6calcsavings,\n    7 => $offer7calcsavings,\n];\n?>\n\n<table>\n    <tr>\n        <th>Offer</th>\n        <th>Savings</th>\n    </tr>\n    <?php\n    foreach ($offers as $offerNumber => $offerCalcsavings) {\n        $savings = $monthlybill - $offerCalcsavings;\n        echo "<tr>\n                <td>Offer $offerNumber</td>\n                <td>$savings</td>\n              </tr>";\n    }\n    ?>\n</table>