$to = 'recipient@example.com';\n$subject = 'Test HTML Email';\n$from = 'sender@example.com';\n\n// To send HTML mail, the Content-type header must be set\n$headers  = "MIME-Version: 1.0\r\n";\n$headers .= "Content-type: multipart/alternative; boundary=\"PHP-alt-".md5(time())."\"\r\n";\n\n// Additional headers\n$headers .= "From: Sender Name <$from>\r\n";\n\n// Message\n$message = "--PHP-alt-".md5(time())."\r\n";\n$message .= "Content-Type: text/plain; charset=\"iso-8859-1\"\r\n";\n$message .= "Content-Transfer-Encoding: 7bit\r\n\r\n";\n$message .= "This is the plain text version of the email.\r\n\r\n";\n$message .= "--PHP-alt-".md5(time())."\r\n";\n$message .= "Content-Type: text/html; charset=\"iso-8859-1\"\r\n";\n$message .= "Content-Transfer-Encoding: 7bit\r\n\r\n";\n$message .= "<p>This is the HTML version of the email.</p>\r\n\r\n";\n$message .= "--PHP-alt-".md5(time())."--\r\n";\n\n// Send\nmail($to, $subject, $message, $headers);