class Model_User extends Model_Person\n{\n    protected static $_userGateway;\n\n    public static function initUserGateway()\n    {\n        if (self::$_userGateway === null) {\n            self::$_userGateway = new Model_Table_User();\n        }\n    }\n\n    public static function getUserByUserName($userName)\n    {\n        self::init();\n        self::initUserGateway();\n\n        $userRow = self::$_userGateway->findByUserName($userName);\n        if ($userRow) {\n            return self::factory($userRow);\n        }\n        return null;\n    }\n\n    public function login()\n    {\n        // Implement login functionality\n    }\n\n    public function setPassword($newPassword)\n    {\n        // Implement set password functionality\n    }\n\n    protected static function factory(Zend_Db_Table_Row $row)\n    {\n        if ($row instanceof Zend_Db_Table_Row) {\n            return new self($row);\n        }\n        return null;\n    }\n}