<?php\n// Define a secret key for basic security\n$secret_key = 'your_secret_key';\n\n// Check if the correct key is provided\nif (!isset($_GET['key']) || $_GET['key'] !== $secret_key) {\n    die('Unauthorized access');\n}\n\n// Command to restart the Tomcat service\n$command = 'sudo systemctl restart tomcat';\n\n// Execute the command\n$output = shell_exec($command);\n\n// Display the result\necho '<pre>' . $output . '</pre>';\n?>