// Example PHP code to load data into staging table\n$conn = oci_connect('username', 'password', 'hostname/dbname');\n$query = "INSERT INTO table_a_staging (id, data) VALUES (:id, :data)";\n$stid = oci_parse($conn, $query);\n\nforeach ($newData as $row) {\n    oci_bind_by_name($stid, ':id', $row['id']);\n    oci_bind_by_name($stid, ':data', $row['data']);\n    oci_execute($stid);\n}\n\noci_free_statement($stid);\noci_close($conn);