<?php\n$xmlUrl = 'http://example.com/file.xml'; // Replace with the actual URL of the XML file\n\n// Check if allow_url_fopen is enabled\nif (ini_get('allow_url_fopen')) {\n    $xmlContent = file_get_contents($xmlUrl);\n\n    if ($xmlContent !== false) {\n        // Use regular expressions to extract content within <title> tags\n        preg_match_all('/<title>(.*?)<\/title>/s', $xmlContent, $matches);\n        $titles = $matches[1];\n\n        // Output the titles array for demonstration purposes\n        print_r($titles);\n    } else {\n        echo 'Failed to load XML content.';\n    }\n} else {\n    echo 'allow_url_fopen is disabled in your PHP configuration.';\n}\n?>