<?php\n// Create a new MySQLi connection\n$mysqli = new mysqli('hostname', 'username', 'password', 'database');\n\n// Prepare the SQL statement\n$stmt = $mysqli->prepare("SELECT col0, col1, col2, col3 FROM table WHERE col3 = ?");\n$stmt->bind_param('s', $param);\n$param = 'value';\n\n// Execute the statement\n$stmt->execute();\n\n// Get the result metadata\n$resultMetaData = $stmt->result_metadata();\n\n// Fetch the field names\n$fields = $resultMetaData->fetch_fields();\n$columnNames = [];\nforeach ($fields as $field) {\n    $columnNames[] = $field->name;\n}\n\n// Bind the result columns to variables\n$bindResults = [];\n$bindParams = [];\nforeach ($columnNames as $key => $colName) {\n    $bindResults[$colName] = null;\n    $bindParams[$key] = &$bindResults[$colName];\n}\n\ncall_user_func_array([$stmt, 'bind_result'], $bindParams);\n\n// Fetch the results\nwhile ($stmt->fetch()) {\n    // Access the results using the column names\n    foreach ($columnNames as $colName) {\n        echo $colName . ': ' . $bindResults[$colName] . "\n";\n    }\n}\n\n// Close the statement and the connection\n$stmt->close();\n$mysqli->close();\n?>