<?php\nfunction getShortDosName($longPath) {\n    $command = 'for %I in ("' . $longPath . '") do @echo %~sI';\n    $output = [];\n    exec($command, $output);\n\n    return isset($output[0]) ? $output[0] : false;\n}\n\n$longPath = 'C:\path\to\your file.txt';\n$shortPath = getShortDosName($longPath);\n\nif ($shortPath) {\n    echo "Short DOS name: " . $shortPath;\n} else {\n    echo "Failed to retrieve short DOS name.";\n}\n?>