<?php\nclass Hello {\n    var $greeting = "hello";\n\n    function hello() {\n        echo $this->greeting;\n        return;\n    }\n}\n\n// Array to keep track of object instances\n$instances = [];\n\n// Create instances and store their names\n$hello1 = new Hello;\n$instances['hello1'] = $hello1;\n\n$hello2 = new Hello;\n$instances['hello2'] = $hello2;\n\n$hello4 = new Hello;\n$instances['hello4'] = $hello4;\n\n// Function to get class names of instances\nfunction getInstanceInfo($instances) {\n    $info = [];\n    foreach ($instances as $name => $instance) {\n        $info[$name] = get_class($instance);\n    }\n    return $info;\n}\n\n// Output the instance information\n$instanceInfo = getInstanceInfo($instances);\nprint_r($instanceInfo);\n?>