class MyClass {\n    private $property;\n\n    public function __construct($value) {\n        $this->property = $value;\n    }\n\n    public function getProperty() {\n        return $this->property;\n    }\n\n    public function setProperty($value) {\n        $this->property = $value;\n    }\n}\n\n$instance = new MyClass('initial value');\necho $instance->getProperty(); // Outputs: initial value\n\n$instance->setProperty('new value');\necho $instance->getProperty(); // Outputs: new value