<?php\n\nclass UserData {\n    private $id;\n    public $name;\n\n    public function __construct($id) {\n        $this->id = $id;\n        $this->loadData();\n    }\n\n    private function loadData() {\n        // Simulating a database query. Replace this with actual database logic.\n        $dummyDatabase = [\n            24 => 'Bob',\n            25 => 'Alice',\n            26 => 'Charlie'\n        ];\n\n        if (isset($dummyDatabase[$this->id])) {\n            $this->name = $dummyDatabase[$this->id];\n        } else {\n            $this->name = 'Unknown User';\n        }\n    }\n}\n\n// Example usage\n$user24 = new UserData(24);\necho 'Welcome, ' . $user24->name . '!';\n\n?>