function removeExcessTags($string) {\n    // Remove <br> and &nbsp; from the start\n    $string = preg_replace('/^(<br\s*\/?>|&nbsp;)+/', '', $string);\n    // Remove <br> and &nbsp; from the end\n    $string = preg_replace('/(<br\s*\/?>|&nbsp;)+$/', '', $string);\n    \n    return $string;\n}\n\n// Example usage\n$inputString = "<br>&nbsp;<br>Example string<br>&nbsp;<br>";\n$outputString = removeExcessTags($inputString);\necho $outputString;  // Output: Example string