function dateExp($timestamp) {\n    $exp = strtotime($timestamp);\n    $today = time();\n\n    return $exp > $today;\n}\n\n// SQL query to get the news articles\n$qry = "SELECT * FROM news WHERE display='Y' ORDER BY priority, date_added, title";\n$news = _execQry($qry);\n\n// Loop through the news articles and display only those which haven't expired\nforeach ($news as $n) {\n    if (dateExp($n['end_date'])) {\n        echo '<h3>' . htmlspecialchars($n['title'], ENT_QUOTES, 'UTF-8') . '</h3>';\n        echo '<p>' . htmlspecialchars($n['story'], ENT_QUOTES, 'UTF-8') . '<br />';\n        echo 'Added on ' . htmlspecialchars(dateFormat($n['date_added']), ENT_QUOTES, 'UTF-8') . '</p><br />';\n    }\n}