<?php\nfunction getVideoDimensions($filePath) {\n    // Ensure the file exists\n    if (!file_exists($filePath)) {\n        throw new Exception("File not found: " . $filePath);\n    }\n\n    // Run the ffprobe command to get video information in JSON format\n    $command = "ffprobe -v error -select_streams v:0 -show_entries stream=width,height -of json \"$filePath\"";\n    $output = shell_exec($command);\n\n    if ($output === null) {\n        throw new Exception("Failed to execute ffprobe command.");\n    }\n\n    // Decode JSON output\n    $data = json_decode($output, true);\n\n    if (json_last_error() !== JSON_ERROR_NONE) {\n        throw new Exception("Failed to decode JSON output.");\n    }\n\n    if (!isset($data['streams'][0]['width']) || !isset($data['streams'][0]['height'])) {\n        throw new Exception("Failed to extract video dimensions.");\n    }\n\n    // Extract width and height\n    $width = $data['streams'][0]['width'];\n    $height = $data['streams'][0]['height'];\n\n    return array('width' => $width, 'height' => $height);\n}\n\ntry {\n    $filePath = '/path/to/your/video.flv'; // Replace with the actual file path\n    $dimensions = getVideoDimensions($filePath);\n    echo "Width: " . $dimensions['width'] . "px, Height: " . $dimensions['height'] . "px";\n} catch (Exception $e) {\n    echo "Error: " . $e->getMessage();\n}\n?>