<?php\n// Make sure to use parameterized queries to prevent SQL injection\n$nid = intval($_GET['nid']); // Assuming you get nid from a GET request, sanitize the input\n\n// Connect to the database using PDO\ntry {\n    $pdo = new PDO('mysql:host=your_host;dbname=your_db', 'your_username', 'your_password');\n    $pdo->setAttribute(PDO::ATTR_ERRMODE, PDO::ERRMODE_EXCEPTION);\n} catch (PDOException $e) {\n    die('Connection failed: ' . $e->getMessage());\n}\n\n// Prepare the query with a JOIN to fetch data from both tables\n$stmt = $pdo->prepare('\n    SELECT tblNavPC.cID, tblNav.NavName \n    FROM tblNavPC \n    JOIN tblNav ON tblNavPC.cID = tblNav.NavID \n    WHERE tblNavPC.PID = :nid\n');\n$stmt->execute(['nid' => $nid]);\n\n// Fetch the results\n$navItems = $stmt->fetchAll(PDO::FETCH_ASSOC);\n\nif ($navItems) {\n    echo '<tr>\n    <td align="left" valign="top" class="medium"><h1>Nav Items</h1></td>\n    </tr>';\n    \n    foreach ($navItems as $navItem) {\n        echo '<tr>\n        <td align="left" valign="top" class="medium">\n        <a href="cat_ap~pnid~' . $nid . '~nid~' . $navItem['cID'] . '~post.htm">' . htmlspecialchars($navItem['NavName'], ENT_QUOTES, 'UTF-8') . '</a>\n        </td>\n        </tr>';\n    }\n} else {\n    echo '<tr><td>No navigation items found.</td></tr>';\n}\n?>