<?php\n     // Path to the wkhtmltopdf binary\n     $wkhtmltopdfPath = '/usr/local/bin/wkhtmltopdf'; // Adjust the path as necessary\n\n     // HTML content to be converted\n     $htmlContent = '<html><head><style>/* Your CSS here */</style></head><body>/* Your HTML here */</body></html>';\n\n     // Temporary files for input HTML and output PDF\n     $inputFile = tempnam(sys_get_temp_dir(), 'html_') . '.html';\n     $outputFile = tempnam(sys_get_temp_dir(), 'pdf_') . '.pdf';\n\n     // Save HTML content to the input file\n     file_put_contents($inputFile, $htmlContent);\n\n     // Command to convert HTML to PDF\n     $command = "$wkhtmltopdfPath $inputFile $outputFile";\n\n     // Execute the command\n     exec($command, $output, $returnVar);\n\n     // Check for successful execution\n     if ($returnVar === 0) {\n         // Read the generated PDF\n         $pdfContent = file_get_contents($outputFile);\n\n         // Output the PDF to the browser\n         header('Content-Type: application/pdf');\n         header('Content-Disposition: inline; filename="document.pdf"');\n         echo $pdfContent;\n     } else {\n         echo "An error occurred while generating the PDF.";\n     }\n\n     // Clean up temporary files\n     unlink($inputFile);\n     unlink($outputFile);\n     ?>