function custom_escape_string($string) {\n    $escaped_string = str_replace(\n        array('\\', "\x00", "\n", "\r", "'", '"', "\x1a"),\n        array('\\\\', '\\0', '\\n', '\\r', "\\'", '\\"', '\\Z'),\n        $string\n    );\n    return $escaped_string;\n}\n\n// Example usage\n$input = "O'Reilly\nNewLine\rCarriageReturn\x1a";\n$escaped_input = custom_escape_string($input);\necho $escaped_input;