<?php\nsession_start();\n\n// Example of storing data in the session\nif (!isset($_SESSION['user_categories'])) {\n    // Fetch categories from the database\n    $userId = $_SESSION['user_id'];\n    $query = "SELECT category_name FROM categories WHERE user_id = :user_id";\n    $stmt = $pdo->prepare($query);\n    $stmt->execute(['user_id' => $userId]);\n    $categories = $stmt->fetchAll(PDO::FETCH_COLUMN);\n\n    // Store categories in the session\n    $_SESSION['user_categories'] = $categories;\n}\n\n// Example of retrieving data from the session\n$categories = $_SESSION['user_categories'];\nforeach ($categories as $category) {\n    echo htmlspecialchars($category) . '<br>';\n}\n?>