<?php\n// Define the namespace and the version\n$namespace = 'http://schemas.microsoft.com/exchange/services/2006/types';\n$version = 'Exchange2007_SP1';\n\n// Create the RequestServerVersion object\n$requestServerVersion = [\n    'Version' => $version\n];\n\n// Create the SoapHeader\n$header = new SoapHeader($namespace, 'RequestServerVersion', $requestServerVersion);\n\n// Create the SoapClient and include the header\n$wsdl = 'path_to_your_wsdl.wsdl';\n$options = [\n    'trace' => 1,\n    'exceptions' => true\n];\n$client = new SoapClient($wsdl, $options);\n$client->__setSoapHeaders($header);\n\n// Now you can make SOAP calls with the client\ntry {\n    $result = $client->YourSoapMethod($params);\n    print_r($result);\n} catch (SoapFault $fault) {\n    echo "Error: {$fault->faultcode}, {$fault->faultstring}";\n}\n?>