for ($i = 0; $i < 100; $i++) {\n    // Establish connection\n    $connection = ads_connect('DataDirectory=\\some\path\;ServerTypes=2;RightsChecking=Off;TrimTrailingSpaces=true;CommType=TCP_IP;', '', '');\n\n    // Execute the first query and free the result\n    $results1 = ads_do($connection, 'SELECT TOP 1 * FROM projects');\n    ads_free_result($results1);\n\n    // Execute the second query and free the result\n    $results2 = ads_do($connection, 'SELECT TOP 1 * FROM projects');\n    ads_free_result($results2);\n\n    // Close the connection\n    ads_close($connection);\n}