// Example UTC time from MySQL\n$utcTime = '2023-10-13 12:00:00';\n\n// User's timezone - you can fetch this based on user's location data\n$userTimezone = 'America/New_York'; // Example timezone\n\n// Create a DateTime object from the UTC time\n$date = new DateTime($utcTime, new DateTimeZone('UTC'));\n\n// Set the timezone to the user's timezone\n$date->setTimezone(new DateTimeZone($userTimezone));\n\n// Format the date to the desired output\n$localTime = $date->format('Y-m-d H:i:s');\n\necho "Local time: " . $localTime;