<?php\nheader('Content-Type: application/json');\n\n$rss_feed_url = 'https://example.com/rss-feed-url.xml';\n$rss_feed_content = file_get_contents($rss_feed_url);\n$rss_feed_xml = simplexml_load_string($rss_feed_content);\n\n$feed_items = [];\nforeach ($rss_feed_xml->channel->item as $item) {\n    $feed_items[] = [\n        'title' => (string) $item->title,\n        'link' => (string) $item->link,\n        'description' => (string) $item->description,\n    ];\n}\n\necho json_encode($feed_items);\n?>