$sql = 'SELECT article_id, title, article FROM journal WHERE article_id = ?';\n$stmt = $conn->stmt_init();\n$stmt->prepare($sql);\n$stmt->bind_param('i', $_GET['article_id']);\n$stmt->bind_result($article_id, $title, $article);\n\n// Execute the query\n$stmt->execute();\n\n// Fetch the result and store in bound variables\n$stmt->fetch();\n\n// $article_id, $title, and $article now contain the data from the row