function custom_site_info_form() {\n     $form = array();\n\n     // Get existing site information form elements\n     $form = system_site_information_settings();\n\n     // Add your custom fields\n     $form['custom_field_1'] = array(\n       '#type' => 'textfield',\n       '#title' => t('Custom Field 1'),\n       '#default_value' => variable_get('custom_field_1', ''),\n       '#description' => t('This is a custom field.'),\n     );\n\n     $form['custom_field_2'] = array(\n       '#type' => 'textarea',\n       '#title' => t('Custom Field 2'),\n       '#default_value' => variable_get('custom_field_2', ''),\n       '#description' => t('This is another custom field.'),\n     );\n\n     // Handle form submission\n     $form['#submit'][] = 'custom_site_info_form_submit';\n\n     return $form;\n   }