<?php\nheader('Content-Type: text/plain; charset=utf-8');\nerror_reporting(E_ALL | E_STRICT);\n\n$context = stream_context_create(array(\n    'http' => array(\n        'method' => 'POST',\n        'header' => implode("\r\n", array(\n            'Content-type: application/x-www-form-urlencoded',\n            'Accept-Language: en-us,en;q=0.5',\n            'Accept-Charset: ISO-8859-1,utf-8;q=0.7,*;q=0.7'\n        )),\n        'content' => http_build_query(array(\n            'prev'  =>  '_t',\n            'hl'    =>  'en',\n            'ie'    =>  'UTF-8',\n            'text'  =>  'hello',\n            'sl'    =>  'en',\n            'tl'    =>  'ru'\n        ))\n    )\n));\n\n$page = file_get_contents('http://translate.google.com/translate_t', false, $context);\n\n// Ensure the response is in UTF-8\n$page = mb_convert_encoding($page, 'UTF-8', 'auto');\n\nrequire '../simplehtmldom/simple_html_dom.php';\n$dom = str_get_html($page);\n$translation = $dom->find('#result_box', 0)->plaintext;\n\n// Ensure the output is in UTF-8\necho mb_convert_encoding($translation, 'UTF-8', 'auto');