<?php\nrequire 'PHPMailerAutoload.php'; // Ensure you have the PHPMailer library included\n\n// Database connection\n$servername = "your_servername";\n$username = "your_username";\n$password = "your_password";\n$dbname = "your_dbname";\n\n$conn = new mysqli($servername, $username, $password, $dbname);\n\n// Check connection\nif ($conn->connect_error) {\n    die("Connection failed: " . $conn->connect_error);\n}\n\n$message = "<html><body> <table><tr><td>HELLO</td></tr></table></body></html>";\n$body = str_replace("[\]", '', $message);\n$mail = new PHPMailer();\n$mail->From = $email;\n$mail->FromName = $Name;\n$mail->CharSet = "utf-8";\n$mail->Subject = "GoodNoon:" . $Name . "";\n$mail->IsHTML(true);\n$mail->AltBody = "To view the message, please use an HTML compatible email viewer";\n$mail->WordWrap = 50;\n$mail->MsgHTML($body);\n$mail->AddAddress("mail@mail.de", "name");\n\nif (!$mail->Send()) {\n    echo "Email Not sent: " . $mail->ErrorInfo;\n} else {\n    echo "Email Sent";\n\n    // Prepare and bind\n    $stmt = $conn->prepare("INSERT INTO emails (email, name, subject) VALUES (?, ?, ?)");\n    $stmt->bind_param("sss", $email, $Name, $mail->Subject);\n\n    // Execute the statement\n    if ($stmt->execute()) {\n        echo "Email data saved to the database.";\n    } else {\n        echo "Error: " . $stmt->error;\n    }\n\n    $stmt->close();\n}\n\n$conn->close();\n?>