<?php\n\nclass A {\n    function fn() {\n        print 'Context: Class:' . get_class($this) \n                . ' Parent:' . get_parent_class($this) . "\n";\n        // No parent::fn() call here since A has no parent\n    }\n}\n\nclass B extends A {\n    function fn() {\n        parent::fn(); // Call parent (A) method\n        print 'Context: Class:' . get_class($this) \n                . ' Parent:' . get_parent_class($this) . "\n";\n    }\n}\n\nclass C extends B {\n    function fn() {\n        parent::fn(); // Call parent (B) method\n        print 'Context: Class:' . get_class($this) \n                . ' Parent:' . get_parent_class($this) . "\n";\n    }\n}\n\n$a = new A();\n$c = new C();\n\n$a->fn();\n\nprint "\n";\n\n$c->fn();\n\n?>