<?php\nrequire_once('xajax_core/xajax.inc.php');\n\n$xajax = new xajax();\n$xajax->configure('javascript URI', 'xajax/');\n\nfunction sendMessage($message) {\n    $response = new xajaxResponse();\n    \n    // Save the message to a file or database\n    file_put_contents('messages.txt', $message . PHP_EOL, FILE_APPEND);\n\n    return $response;\n}\n\nfunction checkMessages() {\n    $response = new xajaxResponse();\n    \n    // Read messages from a file or database\n    $messages = file('messages.txt', FILE_IGNORE_NEW_LINES);\n    $response->assign('chatBox', 'innerHTML', implode('<br>', $messages));\n\n    return $response;\n}\n\n$xajax->register(XAJAX_FUNCTION, 'sendMessage');\n$xajax->register(XAJAX_FUNCTION, 'checkMessages');\n$xajax->processRequest();\n?>\n<!DOCTYPE html>\n<html>\n<head>\n    <title>Real-Time Chat</title>\n    <?php $xajax->printJavascript(); ?>\n    <script type="text/javascript">\n        function sendChatMessage() {\n            var message = document.getElementById('message').value;\n            xajax_sendMessage(message);\n            document.getElementById('message').value = '';\n        }\n\n        function pollMessages() {\n            xajax_checkMessages();\n            setTimeout(pollMessages, 5000); // Poll every 5 seconds\n        }\n\n        window.onload = function() {\n            pollMessages();\n        }\n    </script>\n</head>\n<body>\n    <div id="chatBox"></div>\n    <input type="text" id="message" />\n    <button onclick="sendChatMessage()">Send</button>\n</body>\n</html>