<?php\n// Database connection parameters\n$host = 'localhost';\n$db = 'your_database_name';\n$user = 'your_database_user';\n$pass = 'your_database_password';\n\n// Create a new PDO instance\ntry {\n    $pdo = new PDO("mysql:host=$host;dbname=$db", $user, $pass);\n    $pdo->setAttribute(PDO::ATTR_ERRMODE, PDO::ERRMODE_EXCEPTION);\n} catch (PDOException $e) {\n    die("Could not connect to the database: " . $e->getMessage());\n}\n\n// Get the requested URL\n$requested_url = $_SERVER['REQUEST_URI'];\n\n// Prepare and execute the SQL statement\n$stmt = $pdo->prepare("SELECT new_url FROM url_redirects WHERE old_url = :old_url LIMIT 1");\n$stmt->execute(['old_url' => $requested_url]);\n\n// Fetch the result\n$result = $stmt->fetch(PDO::FETCH_ASSOC);\n\nif ($result) {\n    // If a match is found, issue a 301 redirect\n    header("Location: " . $result['new_url'], true, 301);\n    exit();\n} else {\n    // If no match is found, show a 404 error\n    header("HTTP/1.0 404 Not Found");\n    echo "404 Not Found";\n}\n?>