<?php\nfunction validateYouTubeEmbed($input) {\n    $pattern = '/<iframe.*?src="https:\/\/www\.youtube\.com\/embed\/([a-zA-Z0-9_-]+)".*?<\/iframe>/i';\n    return preg_match($pattern, $input);\n}\n\n// Example usage:\n$input = '<iframe width="560" height="315" src="https://www.youtube.com/embed/VIDEO_ID" frameborder="0" allow="accelerometer; autoplay; encrypted-media; gyroscope; picture-in-picture" allowfullscreen></iframe>';\n\nif (validateYouTubeEmbed($input)) {\n    echo "Valid YouTube embed code.";\n} else {\n    echo "Invalid YouTube embed code.";\n}\n?>