<?php\n// Set the URL to the XML file\n$url = 'http://example.com/yourfile.xml';\n\n// Fetch the XML content from the URL\n$xml_content = file_get_contents($url);\n\nif ($xml_content !== false) {\n    // Load the XML content into a SimpleXMLElement\n    $xml = simplexml_load_string($xml_content);\n\n    if ($xml !== false) {\n        // Successfully loaded the XML, you can now work with it\n        print_r($xml);\n    } else {\n        echo 'Failed to load XML content';\n    }\n} else {\n    echo 'Failed to fetch XML content from URL';\n}\n?>