<?php\n$name = $_POST['name']; // Assuming name is obtained from a form\n$date = date('Y-m-d'); // This will give you today's date in the format YYYY-MM-DD\n\n$subject = "Order for $name on $date";\n$message = "Hello,\n\nYour order has been received.\n\nName: $name\nDate: $date";\n\n$to = "youremail@example.com"; // Replace with your email address\n$headers = "From: no-reply@example.com"; // Replace with appropriate sender email\n\nmail($to, $subject, $message, $headers);\n?>