class Example {\n    public $property = 'Hello, World!';\n    \n    public function displayProperty() {\n        // Correct way to access an instance variable\n        echo $this->property;\n    }\n\n    public function callAnotherMethod() {\n        // Correct way to call another method in the same class\n        $this->displayProperty();\n    }\n}\n\n$example = new Example();\n$example->callAnotherMethod(); // Outputs: Hello, World!