<?php\n$MySQLPassword = "indian";\n$HostName = "localhost";\n$UserName = "monty";\n\n$mysqli = new mysqli($HostName, $UserName, $MySQLPassword, "sachin");\n\nif ($mysqli->connect_error) {\n    die("ERROR: Could not connect to database! (" . $mysqli->connect_error . ")");\n}\n\n$keyword = $_POST['keyword'];\n$sql = "SELECT p.page_url AS url, COUNT(*) AS occurrences\n        FROM page p, word w, occurrence o\n        WHERE p.page_id = o.page_id AND w.word_id = o.word_id AND w.word_word = ?\n        GROUP BY p.page_id\n        ORDER BY occurrences DESC";\n\n$stmt = $mysqli->prepare($sql);\n$stmt->bind_param("s", $keyword);\n$stmt->execute();\n$result = $stmt->get_result();\n\n$output = new DOMDocument();\n$output->formatOutput = true;\n$root = $output->createElement("loginsuccess");\n\nwhile ($row = $result->fetch_assoc()) {\n    $person = $output->createElement("person");\n    $person->appendChild($output->createElement("keyword", htmlspecialchars($keyword)));\n    $person->appendChild($output->createElement("name", htmlspecialchars($row['url'])));\n    $person->appendChild($output->createElement("occur", $row['occurrences']));\n    $root->appendChild($person);\n}\n\n$output->appendChild($root);\n$outputXML = $output->saveXML();\n\n// Save to file\n$file = 'output.xml';\nfile_put_contents($file, $outputXML);\n\nprint($outputXML);\n\n$stmt->close();\n$mysqli->close();\n?>