<?php\n$xml_url = 'http://www.weather.gov/data/current_obs/KBED.xml';\n$cache_file = 'weather_cache.json';\n\nif ($xml = simplexml_load_file($xml_url)) {\n    $weather_data = [\n        'temp' => (string)$xml->temp_f . ' Degrees',\n        'wind' => (string)$xml->wind_mph,\n        'wind_dir' => (string)$xml->wind_dir,\n        'gust' => (string)$xml->wind_gust_mph,\n        'time' => (string)$xml->observation_time,\n        'pres' => (string)$xml->pressure_in,\n        'weath' => (string)$xml->weather\n    ];\n    \n    file_put_contents($cache_file, json_encode($weather_data));\n} else {\n    echo 'unable to load XML file';\n}\n?>