// Example of caching an object in a file\nclass MyObject {\n    public $data;\n\n    public function __construct() {\n        // Expensive operation\n        $this->data = "some expensive data";\n    }\n}\n\n// Cache file path\n$cacheFile = 'cache/myObject.cache';\n\n// Check if cache exists and is fresh\nif (file_exists($cacheFile) && (time() - filemtime($cacheFile) < 3600)) {\n    $myObject = unserialize(file_get_contents($cacheFile));\n} else {\n    // Create a new object and cache it\n    $myObject = new MyObject();\n    file_put_contents($cacheFile, serialize($myObject));\n}\n\n// Use the object\necho $myObject->data;