$filename = "style.css";\n\nif (!file_exists($filename) || !($info = stat($filename))) {\n    header("HTTP/1.1 404 Not Found");\n    die();\n}\n\n// Determine if the browser supports gzip encoding\n$acceptEncoding = $_SERVER['HTTP_ACCEPT_ENCODING'];\n$supportsGzip = stripos($acceptEncoding, "gzip") !== false;\n\nheader("Date: ".gmdate("D, j M Y H:i:s e", time()));\nheader("Cache-Control: max-age=2592000");\nheader("Last-Modified: ".gmdate("D, j M Y H:i:s e", $info['mtime']));\nheader("Etag: ".sprintf("\"%x-%x-%x\"", $info['ino'], $info['size'], $info['mtime']));\nheader("Accept-Ranges: bytes");\nheader("Expires: ".gmdate("D, j M Y H:i:s e", $info['mtime'] + 2592000));\nheader("Content-Type: text/css"); // Ensure correct MIME type for CSS\n\nif ($supportsGzip) {\n    $compressed = gzencode(file_get_contents($filename), 5);\n    header("Content-Encoding: gzip");\n    header("Content-Length: ".strlen($compressed));\n    echo $compressed;\n} else {\n    $content = file_get_contents($filename);\n    header("Content-Length: ".strlen($content));\n    echo $content;\n}\n\ndie();