<?php\nrequire 'includes/connect.php'; // Database connection\n\n$country = intval($_REQUEST['country']); // Ensure the input is an integer\n$q = "SELECT city FROM city WHERE countryid = $country";\n$result = $mysqli->query($q);\n\nif ($result) {\n    echo '<select name="city">';\n    echo '<option>Select City</option>';\n    while ($row = $result->fetch_object()) {\n        $city = htmlspecialchars($row->city, ENT_QUOTES, 'UTF-8'); // Sanitize the city name\n        echo "<option value=\"$city\">$city</option>";\n    }\n    echo '</select>';\n} else {\n    echo 'Error retrieving cities';\n}\n?>