<?php\nfunction replace_excessive_line_breaks($text) {\n    // Use a regular expression to match more than two consecutive line breaks\n    // The regex will match any combination of \r\n, \r, or \n that occur more than twice\n    $pattern = '/(\r\n|\r|\n){3,}/';\n    // Replace them with exactly two line breaks (using \n for simplicity)\n    $replacement = "\n\n";\n\n    // Perform the replacement\n    $text = preg_replace($pattern, $replacement, $text);\n\n    // Optionally, convert line breaks to <br /> for HTML display\n    $text = nl2br($text);\n\n    return $text;\n}\n\n$input_text = "Porcupines\nare\n\n\n\nporcupiney.\n";\n$output_text = replace_excessive_line_breaks($input_text);\necho $output_text;\n?>