require_once 'vendor/autoload.php'; // If using Composer\n// or\nrequire_once 'path/to/nusoap/nusoap.php'; // If manually downloaded\n\n$client = new nusoap_client('http://example.com/soap?wsdl', true);\n\n$error = $client->getError();\nif ($error) {\n    echo 'Constructor error: ' . $error;\n    exit();\n}\n\n$params = array('param1' => 'value1', 'param2' => 'value2');\n$result = $client->call('YourSoapMethod', $params);\n\nif ($client->fault) {\n    echo 'Fault: ';\n    print_r($result);\n} else {\n    $error = $client->getError();\n    if ($error) {\n        echo 'Error: ' . $error;\n    } else {\n        print_r($result);\n    }\n}